             select ifnull(max(test_no),0) + 1 from tb_tests


	    insert into tb_tests 
	    				(
	    					 test_no 
	    					 ,test_name
	    					 ) 

			    values (
			                #{no}
			               ,#{test_name}  
			               )

mapper.update:
    update tb_tests
         set  test_no =  #{test_no}  
              , test_name =  #{test_name}   

	     where  test_no =  #{test_no}

mapper.select:
select test_no ,que_no ,que_content ,opt_1 ,opt_2 ,opt_3 ,opt_4 from tb_testinfo
where test_no = #{test_no}
LIMIT #{pageIndex}, #{pageSize}


			select count(*)
        		  from tb_testinfo	 
        		WHERE  test_no =  #{test_no}

select tt.test_no ,tt.test_name ,ti.que_no ,ti.que_content ,ti.opt_1 ,ti.opt_2 ,ti.opt_3 ,ti.opt_4 ,ti.answer ,ti.score
from tb_testinfo ti inner join tb_tests tt ON tt.test_no = ti.test_no
where ti.test_no = #{test_no} AND que_no = #{que_no}

mapper.insert:


               select ifnull(max(que_no),0) + 1 from tb_testinfo
                 where test_no = #{dt_test_no}


	    insert into tb_testinfo
	    				(
	    					  test_no 
	    					 ,que_no
	    					 ,que_content
	    					 ,opt_1
	    					 ,opt_2
	    					 ,opt_3
	    					 ,opt_4
	    					 ,answer
	    					 ,score
	    					 ) 

			    values (
			                #{dt_test_no}
			                ,#{num}
			               ,#{que_content}  
			               ,#{opt_1}  
			               ,#{opt_2}  
			               ,#{opt_3}  
			               ,#{opt_4}  
			               ,#{answer}  
			               ,#{score}  
			               )

mapper.update:
	    update tb_testinfo
	         set  
	                que_no =  #{que_no}   
	              , que_content =  #{que_content}   
	              , opt_1 =  #{opt_1}   
	              , opt_2 =  #{opt_2}   
	              , opt_3 =  #{opt_3}   
	              , opt_4 =  #{opt_4}   
	              , answer =  #{answer}   
	              , score =  #{score}   
		   where  que_no =  #{que_no}  
   				  and  test_no =  #{dt_test_no}

mapper.delete:
   delete from tb_testinfo
		   where  que_no =  #{que_no}  
		            and  test_no =  #{dt_test_no}
